@article{alsentzer2025few,
  title={Few shot learning for phenotype-driven diagnosis of patients with rare genetic diseases},
  author={Alsentzer, Emily and Li, Michelle M and Kobren, Shilpa N and Noori, Ayush and Undiagnosed Diseases Network and Kohane, Isaac S and Zitnik, Marinka},
  journal={npj Digital Medicine},
  volume={8},
  number={1},
  pages={380},
  year={2025},
  publisher={Nature Publishing Group UK London}
  abstrcat={There are over 7000 rare diseases, some affecting 3500 or fewer patients in the United States. Due to clinicians’ limited experience with such diseases and the heterogeneity of clinical presentations, ~70% of individuals seeking a diagnosis remain undiagnosed. Deep learning has demonstrated success in aiding the diagnosis of common diseases. However, existing approaches require labeled datasets with thousands of diagnosed patients per disease. We present SHEPHERD, a few-shot learning approach for multi-faceted rare disease diagnosis. SHEPHERD performs deep learning over a knowledge graph enriched with rare disease information and is trained on a dataset of simulated rare disease patients. We demonstrate SHEPHERD's effectiveness across diverse diagnostic tasks, performing causal gene discovery, retrieving “patients-like-me”, and characterizing novel disease presentations, using real-world cohorts from the Undiagnosed Diseases Network (N = 465), MyGene2 (N = 146), and the Deciphering Developmental Disorders study (N = 1431). SHEPHERD demonstrates the potential of knowledge-grounded deep learning to accelerate rare disease diagnosis.}
}

@article{kraemer2025artificial,
  title={Artificial intelligence for modelling infectious disease epidemics},
  author={Kraemer, Moritz UG and Tsui, Joseph L-H and Chang, Serina Y and Lytras, Spyros and Khurana, Mark P and Vanderslott, Samantha and Bajaj, Sumali and Scheidwasser, Neil and Curran-Sebastian, Jacob Liam and Semenova, Elizaveta and others},
  journal={Nature},
  volume={638},
  number={8051},
  pages={623--635},
  year={2025},
  publisher={Nature Publishing Group UK London}
  abstract={Infectious disease threats to individual and public health are numerous, varied and frequently unexpected. Artificial intelligence (AI) and related technologies, which are already supporting human decision making in economics, medicine and social science, have the potential to transform the scope and power of infectious disease epidemiology. Here we consider the application to infectious disease modelling of AI systems that combine machine learning, computational statistics, information retrieval and data science. We first outline how recent advances in AI can accelerate breakthroughs in answering key epidemiological questions and we discuss specific AI methods that can be applied to routinely collected infectious disease surveillance data. Second, we elaborate on the social context of AI for infectious disease epidemiology, including issues such as explainability, safety, accountability and ethics. Finally, we summarize some limitations of AI applications in this field and provide recommendations for how infectious disease epidemiology can harness most effectively current and future developments in AI.}
}

@article{martinez2022raredis,
  title={The RareDis corpus: a corpus annotated with rare diseases, their signs and symptoms},
  author={Mart{\'\i}nez-deMiguel, Claudia and Segura-Bedmar, Isabel and Chac{\'o}n-Solano, Esteban and Guerrero-Aspizua, Sara},
  journal={Journal of biomedical informatics},
  volume={125},
  pages={103961},
  year={2022},
  publisher={Elsevier}
  abstract={Rare diseases affect a small number of people compared to the general population. However, more than 6,000 different rare diseases exist and, in total, they affect more than 300 million people worldwide. Rare diseases share as part of their main problem, the delay in diagnosis and the sparse information available for researchers, clinicians, and patients. Finding a diagnostic can be a very long and frustrating experience for patients and their families. The average diagnostic delay is between 6-8 years. Many of these diseases result in different manifestations among patients, which hampers even more their detection and the correct treatment choice. Therefore, there is an urgent need to increase the scientific and medical knowledge about rare diseases. Natural Language Processing (NLP) can help to extract relevant information about rare diseases to facilitate their diagnosis and treatments, but most NLP techniques require manually annotated corpora. Therefore, our goal is to create a gold standard corpus annotated with rare diseases and their clinical manifestations. It could be used to train and test NLP approaches and the information extracted through NLP could enrich the knowledge of rare diseases, and thereby, help to reduce the diagnostic delay and improve the treatment of rare diseases. The paper describes the selection of 1,041 texts to be included in the corpus, the annotation process and the annotation guidelines. The entities (disease, rare disease, symptom, sign and anaphor) and the relationships (produces, is a, is acron, is synon, increases risk of, anaphora) were annotated. The RareDis corpus contains more than 5,000 rare diseases and almost 6,000 clinical manifestations are annotated. Moreover, the Inter Annotator Agreement evaluation shows a relatively high agreement (F1-measure equal to 83.5% under exact match criteria for the entities and equal to 81.3% for the relations). Based on these results, this corpus is of high quality, supposing a significant step for the field since there is a scarcity of available corpus annotated with rare diseases. This could open the door to further NLP applications, which would facilitate the diagnosis and treatment of these rare diseases and, therefore, would improve dramatically the quality of life of these patients.}
}
