@article{ju2024monica,
  title={MONICA: Benchmarking on Long-tailed Medical Image Classification},
  author={Ju, Lie and Yan, Siyuan and Zhou, Yukun and Nan, Yang and Xing, Xiaodan and Duan, Peibo and Ge, Zongyuan},
  journal={arXiv preprint arXiv:2410.02010},
  year={2024}
  abstract={Long-tailed learning is considered to be an extremely challenging problem in data imbalance learning. It aims to train well-generalized models from a large number of images that follow a long-tailed class distribution. In the medical field, many diagnostic imaging exams such as dermoscopy and chest radiography yield a long-tailed distribution of complex clinical findings. Recently, long-tailed learning in medical image analysis has garnered significant attention. However, the field currently lacks a unified, strictly formulated, and comprehensive benchmark, which often leads to unfair comparisons and inconclusive results. To help the community improve the evaluation and advance, we build a unified, well-structured codebase called Medical OpeN-source Long-taIled ClassifiCAtion (MONICA), which implements over 30 methods developed in relevant fields and evaluated on12 long-tailed medical datasets covering 6 medical domains. Our work provides valuable practical guidance and insights for the field, offering detailed analysis and discussion on the effectiveness of individual components within the inbuilt stateof-the-art methodologies. We hope this codebase serves as a comprehensive and reproducible benchmark, encouraging further advancements in long-tailed medical image learning. The codebase is publicly available on https://github.com/ PyJulie/MONICA.}
}

@article{wu2024medical,
  title={Medical long-tailed learning for imbalanced data: Bibliometric analysis},
  author={Wu, Zheng and Guo, Kehua and Luo, Entao and Wang, Tian and Wang, Shoujin and Yang, Yi and Zhu, Xiangyuan and Ding, Rui},
  journal={Computer Methods and Programs in Biomedicine},
  volume={247},
  pages={108106},
  year={2024},
  publisher={Elsevier}
  abstract={In the last decade, long-tail learning has become a popular research focus in deep learning applications in medicine. However, no scientometric reports have provided a systematic overview of this scientific field. We utilized bibliometric techniques to identify and analyze the literature on long-tailed learning in deep learning applications in medicine and investigate research trends, core authors, and core journals. We expanded our understanding of the primary components and principal methodologies of long-tail learning research in the medical field.}
}
